target.query:
from(bucket: "${bucket}")
  |> range(start: -1y)
  |> filter(fn: (r) => r["_measurement"] == "NewEpoch")
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({
      time: r._time,
      text: "New Epoch " + string(v: r.epoch) + " at block " + string(v: r.block),
      title: "Epoch Start",
    }))

targets.query:
from(bucket: "${bucket}")
|>range(start: ${__from}/1000, stop: ${__to}/1000)
|>filter(fn:(r)=>r["_measurement"]=="validatorToOracle")
|>pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
//|> keep(columns: ["_time", "address", "ip", "block"])
|> group()
|> map(fn: (r) => ({
      r with
      index: int(v: r.index)
    }))
|> sort(columns:["index"])
